import React, {Component} from 'react';
import './index.css'
import profile from "./profile.png";
import AddReply from "./AddReply";
// import profile from './profile.png';
// import AddReply from "./AddReply";
// import ApiService from "./apiService";


class Replies extends Component {

  state = {

    users: [["Alfred", "85af2c11-9ec1-4d1a-a1f7-90f05cffc73c"],
      ["Rodger", "1454bb0c-d9df-4bb6-9599-887d60d2f748"],
      ["Lindsay", "6d22a6c9-65cc-4a1a-a509-baf59885feeb"],
      ["Quincy", "a4c083eb-b8cd-4669-8a54-ac14fd567129"],
      ["Jeff", "c2d5c3db-8288-4761-bf6f-a99e06ea2982"]
    ],
    showReplyForm: false
  };

  toggleShowReplyForm = () => {
    this.setState({
      showReplyForm: !this.state.showReplyForm
    });
  };

  render() {
    return (
    <div className="container">
      <div className="card my-3">
        <div className="card-body">
          <div className="cf">
            <img src={profile} alt="pic" className="pic"/>
            <h2 className="card-title">{this.props.getUser(this.props.r)}</h2>
          </div>
          <h4 className="card-title">{this.props.r.body}</h4>
          <button>

          </button>
          <button className="btn-info float-left btn-sm" style={{fontSize: '18px'}}
                  onClick={() => this.props.addLike(this.props.r.id)}>Likes: {this.props.r.likes}</button>
          <button className="btn btn-danger float-right btn-sm" style={{fontSize: '18px'}}
                  onClick={() => this.props.deleteComment(this.props.r.id)}>Delete
          </button>
          <button className="btn btn-primary float-right btn-sm">Edit</button>
        </div>
      </div>
      {this.state.showReplyForm && <AddReply storeReply={this.storeReply} users={this.state.users} parentCommentId={this.props.r.comment_id} />}

    </div>
    )
  }
}
export default Replies
