import React, {Component} from 'react';
import './index.css'
import profile from './profile.png';
import {v4 as uuidv4} from "uuid";

class Comment extends Component {

  state = {
    body: '',
    //hard coding post id because there's only one post
    post_id: '9ba7b1d5-94e0-41bd-a380-e2f953ffe58c',
    comment_id: uuidv4(),
    parent_id: '',
    likes: 0,
    //Default poster is Alfred b/c he's at the top of the list
    poster: '85af2c11-9ec1-4d1a-a1f7-90f05cffc73c',
    id: '',
    created_at: '',
    updated_at: ''
  }

  handleFieldChange = (comment) => {
    this.setState({
      [comment.target.name]: comment.target.value
    })
  }

  createComment = () => {
    this.props.storeComment(this.state)
  }

  handlePosterChange = (comment) => {
    this.setState({poster: comment.target.value});
  }
  render({comment, deleteComment, addLike, getUser}) {
    return (
      <div className="card my-3">
        <div className="card-body">
          <div className="cf">
            <img src={profile} alt="pic" className="pic"/>
            <h2 className="card-title">{this.props.getUser(comment)}</h2>
          </div>
          <h4 className="card-title">{comment.body}</h4>
          <button className="btn-primary">Edit</button>
          <button className="btn-warning">Reply</button>
          <button className="btn-info float-left btn-sm" style={{fontSize: '18px'}}
                  onClick={() => addLike(comment.id)}>Likes: {comment.likes}</button>
          <button className="btn btn-danger float-right btn-sm" style={{fontSize: '18px'}}
                  onClick={() => deleteComment(comment.id)}>Delete
          </button>
        </div>
      </div>
    )
  }
}
export default Comment
