import React, { Component } from 'react';
import vid from './video.gif';
import './index.css'

import Comment from './Comment';
import AddComment from './AddComment';
import ApiService from './apiService';

class App extends Component {
	state = {
		comments: [],
    users: [["Alfred", "85af2c11-9ec1-4d1a-a1f7-90f05cffc73c","https://i.pravatar.cc/300"],
            ["Rodger", "1454bb0c-d9df-4bb6-9599-887d60d2f748","https://i.pravatar.cc/300"],
            ["Lindsay", "6d22a6c9-65cc-4a1a-a509-baf59885feeb","https://i.pravatar.cc/300"],
            ["Quincy", "a4c083eb-b8cd-4669-8a54-ac14fd567129","https://i.pravatar.cc/300"],
            ["Jeff", "c2d5c3db-8288-4761-bf6f-a99e06ea2982","https://i.pravatar.cc/300"]
    ],
    userPics: [["Alfred","https://i.pravatar.cc/300"],
      ["Rodger","https://i.pravatar.cc/300"],
      ["Lindsay","https://i.pravatar.cc/300"],
      ["Quincy","https://i.pravatar.cc/300"],
      ["Jeff","https://i.pravatar.cc/300"]
    ],
		showCreateCommentForm: false
	};

	constructor(props) {
		super(props);

		this.apiService = new ApiService();
	}

	componentWillMount() {
		this.fetchComments();
	}

	fetchComments = () =>
		this.apiService.getComments().then(({ data }) => {
			this.setState({
        comments: data.comments
			});
    });

  storeComment = (data) => {
    this.apiService.storeComment(data)
      .then(() => {
        this.fetchComments()
        this.toggleShowCreateCommentForm()
      })
  }

  deleteComment = (comment_id) => {
    this.apiService.deleteComment(comment_id)
      .then(() => {
        this.fetchComments()
      })
  }

  addLike = (comment_id) => {
    this.apiService.addLike(comment_id)
      .then(() => {
        this.fetchComments()
      })
  }

  getUser = (comment) => {
    const userUUID = comment.poster;
    let i;
    let userName = "";
    for(i=0; i < this.state.users.length; i++)
    {
      if(this.state.users[i][1] === userUUID)
      {
        userName = this.state.users[i][0]
      }
    }
      return userName;
  }

  setPic = () => {
    const c = this.getUser()
    var j;
    let pic = "";
    for(j=0; j < this.state.userPics.length; j++)
    {
      if(this.state.userPics[j][1] === c)
      {
        pic = this.state.userPics[j][0]
      }
    }
    return pic;
  }

	toggleShowCreateCommentForm = () => {
		this.setState({
			showCreateCommentForm: !this.state.showCreateCommentForm
		});
	};

	render() {
		return (
			<div className="container">
				<h1 className="text-center my-5">Comment Api</h1>
        <img src={vid} alt="video" className="vid"/>
        <br/>
				<div className="text-center mb-5">
					<button
						className={`btn ${this.state.showCreateCommentForm ? 'btn-danger' : 'btn-info'}`}
						onClick={this.toggleShowCreateCommentForm}>
						{this.state.showCreateCommentForm ? 'Cancel' : 'Add Comment'}
					</button>
				</div>
				{this.state.showCreateCommentForm && <AddComment storeComment={this.storeComment} users={this.state.users} />}
				{this.state.comments.map((comment) => <Comment deleteComment={this.deleteComment} addLike={this.addLike} getUser={this.setUser} getPic={this.getPic()} comment={comment} key={comment.comment_id}/>)}
			</div>
		);
	}

}

export default App;
