import React from 'react';

const Event = ({ comment, deleteEvent }) => ((
  <div className="card my-3">
    <div className="card-body">
      <h4 className="card-title">{comment.body}</h4>
      <button className="btn btn-danger float-right btn-sm" onClick={() => deleteEvent(comment.post_id)}>Delete</button>
    </div>
  </div>
))

export default Event
