import React, {Component} from 'react';
import './index.css'
import profile from './profile.png';
import AddReply from "./AddReply";
import ApiService from "./apiService";
import Replies from "./Replies";


class Comment extends Component {

  state = {

    replies:
    {
      "body": "Oh my I could stare at that all day!",
      "post_id": "9ba7b1d5-94e0-41bd-a380-e2f953ffe58c",
      "comment_id": "1f697624-f920-40c5-95e8-84b10dbed82c",
      "parent_id": null,
      "likes": 28,
      "poster": "a4c083eb-b8cd-4669-8a54-ac14fd567129",
      "id": 94,
      "created_at": "2021-03-24 18:29:44",
      "updated_at": "2021-03-26 10:12:02"
    },
    showReplyForm: false
  };
  constructor(props) {
    super(props);

    this.apiService = new ApiService();
  }

  fetchReplies = () =>
    this.apiService.getComments().then(({ data }) => {
      this.setState({
        replies: data.comments
      });
    });

  componentWillMount() {
    this.fetchReplies();
  }

  storeReply = (data) => {
    this.apiService.storeComment(data).then(() => {
      this.fetchReplies()
      this.toggleShowReplyForm()
    })
  }

  toggleShowReplyForm = () => {
    this.setState({
      showReplyForm: !this.state.showReplyForm
    });
  };

  render() {
    return (
      <div className="container">
      <div className="card my-3">
        <div className="card-body">
          <div className="cf">
            <img src={profile} alt="pic" className="pic"/>
            <h2 className="card-title">{this.props.getUser(this.props.comment)}</h2>
          </div>
          <h4 className="card-title">{this.props.comment.body}</h4>
          <h3>{this.props.comment.comment_id}</h3>
          <button
            className={`btn ${this.state.showReplyForm ? 'btn-danger' : 'btn-warning'}`}
            onClick={this.toggleShowReplyForm}>
            {this.state.showReplyForm ? 'Cancel' : 'Reply'}
          </button>
          <button className="btn-info float-left btn-sm" style={{fontSize: '18px'}}
                  onClick={() => this.props.addLike(this.props.comment.id)}>Likes: {this.props.comment.likes}</button>
          <button className="btn btn-danger float-right btn-sm" style={{fontSize: '18px'}}
                  onClick={() => this.props.deleteComment(this.props.comment.id)}>Delete
          </button>
          <button className="btn btn-primary float-right btn-sm">Edit</button>
        </div>
      </div>
        {this.state.showReplyForm && <AddReply storeReply={this.storeReply} users={this.state.users} parentCommentId={this.props.comment.comment_id} />}
        <Replies deleteComment={this.deleteComment} addLike={this.addLike} getUser={this.getUser} replies={this.state.replies} key={this.state.replies.comment_id}/>)
      </div>
    )
  }
}
export default Comment
