import styled from '@emotion/styled'
import { css, get } from '@theme-ui/css'
import { createShouldForwardProp } from '@styled-system/should-forward-prop'
import space from '@styled-system/space'
import color from '@styled-system/color'

const boxSystemProps = [...space.propNames, ...color.propNames]

/**
 * @internal
 * @type {(prop: string) => boolean}
 */
export const __isBoxStyledSystemProp = (prop) => boxSystemProps.includes(prop)

const shouldForwardProp = createShouldForwardProp(boxSystemProps)

const sx = (props) => css(props.sx)(props.theme)
const base = (props) => css(props.__css)(props.theme)
const variant = ({ theme, variant, __themeKey = 'variants' }) =>
  css(get(theme, __themeKey + '.' + variant, get(theme, variant)))

export const Box = styled('div', {
  shouldForwardProp,
})(
  {
    boxSizing: 'border-box',
    margin: 0,
    minWidth: 0,
  },
  base,
  variant,
  space,
  color,
  sx,
  (props) => props.css
)

Box.displayName = 'Box'

export default Box
import styled from '@emotion/styled'
import Box from './Box'

export const Flex = styled(Box)({
  display: 'flex',
})

Flex.displayName = 'Flex'
import React from 'react'
import Box from './Box'

const px = (n) => (typeof n === 'number' ? n + 'px' : n)

const widthToColumns = (width, repeat) =>
  Array.isArray(width)
    ? width.map((w) => widthToColumns(w, repeat))
    : !!width && `repeat(auto-${repeat}, minmax(${px(width)}, 1fr))`

const countToColumns = (n) =>
  Array.isArray(n)
    ? n.map(countToColumns)
    : !!n && (typeof n === 'number' ? `repeat(${n}, 1fr)` : n)

export const Grid = React.forwardRef(function Grid(
  { width, columns, gap = 3, repeat = 'fit', ...props },
  ref
) {
  const gridTemplateColumns = !!width
    ? widthToColumns(width, repeat)
    : countToColumns(columns)

  return (
    <Box
      ref={ref}
      {...props}
      __themeKey="grids"
      __css={{
        display: 'grid',
        gridGap: gap,
        gridTemplateColumns,
      }}
    />
  )
})
import React from 'react'
import Box from './Box'

export const Button = React.forwardRef(function Button(props, ref) {
  return (
    <Box
      ref={ref}
      as="button"
      variant="primary"
      {...props}
      __themeKey="buttons"
      __css={{
        appearance: 'none',
        display: props.hidden ? undefined : 'inline-block',
        textAlign: 'center',
        lineHeight: 'inherit',
        textDecoration: 'none',
        fontSize: 'inherit',
        px: 3,
        py: 2,
        color: 'white',
        bg: 'primary',
        border: 0,
        borderRadius: 4,
      }}
    />
  )
})
import React from 'react'
import Box from './Box'

export const Link = React.forwardRef(function Link(props, ref) {
  return (
    <Box ref={ref} as="a" variant="styles.a" {...props} __themeKey="links" />
  )
})
import React from 'react'
import Box from './Box'

export const Paragraph = React.forwardRef(function Paragraph(
  { sx, ...props },
  ref
) {
  return (
    <Box
      ref={ref}
      as="p"
      variant="paragraph"
      {...props}
      sx={{
        // reset margin by default: avoid relying on user-agent margins (not aware of theme-ui space scale)
        margin: 0,
        // set a max-width: avoid full-page paragraphs
        '@media screen and (min-width: 36em)': {
          maxWidth: '48rem',
        },
        ...sx,
      }}
      __themeKey="text"
      __css={{
        fontFamily: 'body',
        fontWeight: 'body',
        lineHeight: 'body',
      }}
    />
  )
})
import React from 'react'
import Box from './Box'

export const Text = React.forwardRef(function Text(props, ref) {
  return (
    <Box as="span" ref={ref} variant="default" {...props} __themeKey="text" />
  )
})
