      variant="heading"
      {...props}
      __themeKey="text"
      __css={{
        fontFamily: 'heading',
        fontWeight: 'heading',
        lineHeight: 'heading',
      }}
    />
  )
})
import React from 'react'
import Box from './Box'

export const Image = React.forwardRef(function Image(props, ref) {
  return (
    <Box
      ref={ref}
      as="img"
      {...props}
      __themeKey="images"
      __css={{
        maxWidth: '100%',
        height: 'auto',
        ...props.__css,
      }}
    />
  )
})
import React from 'react'
import Box from './Box'

export const Card = React.forwardRef(function Card(props, ref) {
  return <Box ref={ref} variant="primary" {...props} __themeKey="cards" />
})
import React from 'react'
import Box from './Box'

export const Label = React.forwardRef(function Label(props, ref) {
  return (
    <Box
      ref={ref}
      as="label"
      variant="label"
      {...props}
      __themeKey="forms"
      __css={{
        width: '100%',
        display: 'flex',
      }}
    />
  )
})
import React from 'react'
import Box from './Box'

export const Input = React.forwardRef(function Input(props, ref) {
  return (
    <Box
      ref={ref}
      as="input"
      variant="input"
      {...props}
      __themeKey="forms"
      __css={{
        display: 'block',
        width: '100%',
        p: 2,
        appearance: 'none',
        fontSize: 'inherit',
        lineHeight: 'inherit',
        border: '1px solid',
        borderRadius: 4,
        color: 'inherit',
        bg: 'transparent',
      }}
    />
  )
})
import React from 'react'
import Box from './Box'

const SVG = ({ size = 24, ...props }) => (
  <Box
    as="svg"
    xmlns="http://www.w3.org/2000/svg"
    width={size + ''}
    height={size + ''}
    viewBox="0 0 24 24"
    fill="currentcolor"
    {...props}
  />
)

SVG.displayName = 'SVG'

export default SVG
export const getProps = (test) => (props) => {
  const next = {}
  for (const key in props) {
    if (test(key || '')) next[key] = props[key]
  }
  return next
}

const MRE = /^m[trblxy]?$/

export const getMargin = getProps((k) => MRE.test(k))
export const omitMargin = getProps((k) => !MRE.test(k))
import React from 'react'
import Box from './Box'
import SVG from './SVG'
import { getMargin, omitMargin } from './util'

const DownArrow = (props) => (
  <SVG {...props}>
    <path d="M7 10l5 5 5-5z" />
  </SVG>
)

export const Select = React.forwardRef(function Select(
  { arrow, ...props },
  ref
) {
  return (
    <Box
      {...getMargin(props)}
      sx={{
        display: 'flex',
      }}>
      <Box
        ref={ref}
        as="select"
        variant="select"
        {...omitMargin(props)}
        __themeKey="forms"
        __css={{
          display: 'block',
          width: '100%',
          p: 2,
          appearance: 'none',
          fontSize: 'inherit',
          lineHeight: 'inherit',
          border: '1px solid',
          borderRadius: 4,
          color: 'inherit',
          bg: 'transparent',
        }}
      />
      {arrow || (
        <DownArrow
          sx={{
            ml: -28,
            alignSelf: 'center',
            pointerEvents: 'none',
          }}
        />
      )}
    </Box>
  )
})
import React from 'react'
import Box from './Box'

export const Textarea = React.forwardRef(function Textarea(props, ref) {
  return (
    <Box
      ref={ref}
      as="textarea"
      variant="textarea"
      {...props}
      __themeKey="forms"
      __css={{
        display: 'block',
        width: '100%',
        p: 2,
        appearance: 'none',
        fontSize: 'inherit',
        lineHeight: 'inherit',
        border: '1px solid',
        borderRadius: 4,
        color: 'inherit',
        bg: 'transparent',
      }}
    />
  )
})
import React from 'react'
import Box from './Box'
import SVG from './SVG'

const RadioChecked = (props) => (
  <SVG {...props}>
    <path d="M12 7c-2.76 0-5 2.24-5 5s2.24 5 5 5 5-2.24 5-5-2.24-5-5-5zm0-5C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm0 18c-4.42 0-8-3.58-8-8s3.58-8 8-8 8 3.58 8 8-3.58 8-8 8z" />
  </SVG>
)

const RadioUnchecked = (props) => (
  <SVG {...props}>
    <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm0 18c-4.42 0-8-3.58-8-8s3.58-8 8-8 8 3.58 8 8-3.58 8-8 8z" />
  </SVG>
)

const RadioIcon = (props) => (
  <React.Fragment>
    <RadioChecked
      {...props}
      __css={{
        display: 'none',
        'input:checked ~ &': {
          display: 'block',
        },
      }}
    />
    <RadioUnchecked
      {...props}
      __css={{
        display: 'block',
        'input:checked ~ &': {
          display: 'none',
        },
      }}
    />
  </React.Fragment>
)

export const Radio = React.forwardRef(function Radio(
  { className, sx, variant = 'radio', ...props },
  ref
) {
  return (
    <Box sx={{ minWidth: 'min-content' }}>
      <Box
        ref={ref}
        as="input"
        type="radio"
        {...props}
        sx={{
          position: 'absolute',
          opacity: 0,
          zIndex: -1,
          width: 1,
          height: 1,
          overflow: 'hidden',
        }}
      />
      <Box
        as={RadioIcon}
        aria-hidden="true"
        __themeKey="forms"
        variant={variant}
        className={className}
        sx={sx}
        __css={{
          // todo: system props??
          mr: 2,
          borderRadius: 9999,
          color: 'gray',
          flexShrink: 0,
          'input:checked ~ &': {
            color: 'primary',
          },
          'input:focus ~ &': {
            bg: 'highlight',
          },
        }}
      />
    </Box>
  )
})
import React from 'react'
import Box from './Box'
import SVG from './SVG'

const CheckboxChecked = (props) => (
  <SVG {...props}>
    <path d="M19 3H5c-1.11 0-2 .9-2 2v14c0 1.1.89 2 2 2h14c1.11 0 2-.9 2-2V5c0-1.1-.89-2-2-2zm-9 14l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z" />
  </SVG>
)

const CheckboxUnchecked = (props) => (
  <SVG {...props}>
    <path d="M19 5v14H5V5h14m0-2H5c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2z" />
  </SVG>
)

const CheckboxIcon = (props) => (
  <React.Fragment>
    <CheckboxChecked
      {...props}
      __css={{
        display: 'none',
        'input:checked ~ &': {
          display: 'block',
        },
      }}
    />
    <CheckboxUnchecked
      {...props}
      __css={{
        display: 'block',
        'input:checked ~ &': {
          display: 'none',
        },
      }}
    />
  </React.Fragment>
)

export const Checkbox = React.forwardRef(function Checkbox(
  { className, sx, variant = 'checkbox', children, ...props },
  ref
) {
  return (
    <Box sx={{ minWidth: 'min-content' }}>
      <Box
        ref={ref}
        as="input"
        type="checkbox"
        {...props}
        sx={{
          position: 'absolute',
          opacity: 0,
          zIndex: -1,
          width: 1,
          height: 1,
          overflow: 'hidden',
        }}
      />
      <Box
        as={CheckboxIcon}
        aria-hidden="true"
        __themeKey="forms"
        variant={variant}
        className={className}
        sx={sx}
        __css={{
          mr: 2,
          borderRadius: 4,
          color: 'gray',
          flexShrink: 0,
          'input:checked ~ &': {
            color: 'primary',
          },
          'input:focus ~ &': {
            color: 'primary',
            bg: 'highlight',
          },
        }}
      />
      {children}
    </Box>
  )
})
import React from 'react'
import { Box } from './Box'
import { Label } from './Label'

const GUTTER = 2
const SIZE = 18

export const Switch = React.forwardRef(function Switch(
  { className, label, sx, variant = 'switch', ...props },
  ref
) {
  return (
    <Label sx={{ cursor: 'pointer' }}>
      <Box
        ref={ref}
        as="input"
        type="checkbox"
        __themeKey="forms"
        aria-label={label}
        {...props}
        sx={{
          position: 'absolute',
          opacity: 0,
          zIndex: -1,
          width: 1,
          height: 1,
          overflow: 'hidden',
        }}
      />
      <Box
        css={{
          padding: GUTTER,
        }}
        __themeKey="forms"
        variant={variant}
        className={className}
        sx={sx}
        __css={{
          position: 'relative',
          bg: 'gray',
          borderRadius: SIZE,
          height: SIZE + GUTTER * 2,
          width: SIZE * 2 + GUTTER * 2,
          mr: 2,
          'input:disabled ~ &': {
            opacity: 0.5,
            cursor: 'not-allowed',
          },
          '& > div': {
            display: 'flex',
            alignItems: 'center',
            borderRadius: '50%',
            height: SIZE,
            width: SIZE,
            bg: 'white',
            boxShadow: '0 1px 2px rgba(0, 0, 0, 0.1)',
            position: 'relative',
            transform: 'translateX(0%)',
            transition: `transform 240ms cubic-bezier(0.165, 0.840, 0.440, 1.000)`,
          },
          'input:checked ~ &': {
            bg: 'primary',
            '> div': {
              transform: 'translateX(100%)',
            },
          },
        }}>
        <Box />
      </Box>
      <span>{label}</span>
    </Label>
  )
})
import React from 'react'
import Box from './Box'

const thumb = {
  appearance: 'none',
