import React, {Component} from 'react';
import './index.css'
// import profile from './profile.png';
// import AddReply from "./AddReply";
// import ApiService from "./apiService";


class Replies extends Component {

  state = {

    reply: []

  }

  constructor() {
    super();

    this.getReplies(this.props.replies.parent_id)

  }

  getReplies = (parent_id) =>
    this.apiService.getComments().then(({ parent_id }) => {
      this.setState({
        reply: parent_id.comments
      });
    });


  render() {
    return (
      <div className="container">
        <div className="card my-3">

          <h1>{this.props.replies.body}</h1>
          <h1>Parent id: {this.props.replies.parent_id}</h1>
          <h1></h1>

        </div>
      </div>
    )
  }
}
export default Replies
