import React, { Component } from 'react'

import { v4 as uuidv4 } from 'uuid';
import './index.css'
import profile from "./profile.png";

class EditComment extends Component {

  state = {
    body: '',
    //hard coding post id because there's only one post
    post_id: '9ba7b1d5-94e0-41bd-a380-e2f953ffe58c',
    comment_id: uuidv4(),
    parent_id: this.props.parentCommentId,
    likes: 0,
    poster: uuidv4(),
    poster_name: '',
    id: '',
    created_at: '',
    updated_at: ''
  }

  handleFieldChange = (comment) => {
    this.setState({
      [comment.target.name]: comment.target.value
    })
  }

  render() {
    return (
      <div className="container">
        <div className="card my-33">
          <div className="card-body 33">
            <div className="cf 33">
              <img src={profile} alt="pic" className="pic"/>
              <h2 className="card-title">{this.props.userInfo.poster_name}</h2>
              <textarea className="edit" name="body" onChange={this.handleFieldChange}>{this.props.userInfo.body}</textarea>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default EditComment
