import React, { Component } from 'react'

import { v4 as uuidv4 } from 'uuid';
import './index.css'

class AddComment extends Component {

  state = {
    //Hard coded so there will always be something in that field
    body: '',
    //hard coding post id because there's only one post
    post_id: '9ba7b1d5-94e0-41bd-a380-e2f953ffe58c',
    comment_id: uuidv4(),
    parent_id: '',
    likes: 0,
    poster: uuidv4(),
    poster_name: '',
    id: '',
    created_at: '',
    updated_at: '',

    errors:{
      name: '',
      body: '',
    }
  }

   requiredValidator = (val) => {
     if (!val) {
       return ["This field is required"];
     }

     return [];

   }

  handleFieldChange = (comment) => {
    this.setState({
      [comment.target.name]: comment.target.value
    })
  }

  createComment = () => {
      this.props.storeComment(this.state)
  }

   handleKeypress = (e) => {
    if (e.keyCode === 13) {
      this.createComment()
    }
  };

  render() {
    const {errors} = this.state;
    return (
      <div className="container">
        <div className="row">
          <div className="col-md-6 offset-md-3">
            <h4 className="text-center mb-5">Add new Comment</h4>
            <form onSubmit={this.createComment}>
            <div className="form-group">
              <label>
                <input name="poster_name" onChange={this.handleFieldChange} validators={[this.requiredValidator]} type="text" className="form-control" placeholder="Name" noValidate />
                {errors.name.length > 0 &&
                <span className='error'>{errors.name}</span>}
              </label>
              <textarea name="body" onChange={this.handleFieldChange} type="text" className="form-control body" placeholder="Body" noValidate />
              {errors.body.length > 0 &&
              <span className='error'>{errors.body}</span>}
            </div>
            <div className="submit">
              <button className="btn btn-success">Post Comment</button>
            </div>
          </form>
          </div>
        </div>
      </div>
    )
  }
}

export default AddComment
