import React, {Component} from 'react';
import './index.css'
import profile from './profile.png';
import AddReply from "./AddReply";
import ApiService from "./apiService";


class Replies extends Component {


  render() {
    return (
      <div className="container">
        <div className="card my-3">
          <div className="card-body">
            <div className="cf">
              <img src={profile} alt="pic" className="pic"/>
              <h2 className="card-title">{this.props.getUser(this.props.comment)}</h2>
            </div>
            <h4 className="card-title">{this.props.comment.body}</h4>
            <h3>{this.props.comment.comment_id}</h3>
            <button
              className={`btn ${this.state.showReplyForm ? 'btn-danger' : 'btn-warning'}`}
              onClick={this.toggleShowReplyForm}>
              {this.state.showReplyForm ? 'Cancel' : 'Reply'}
            </button>
            <button className="btn-info float-left btn-sm" style={{fontSize: '18px'}}
                    onClick={() => this.props.addLike(this.props.comment.id)}>Likes: {this.props.comment.likes}</button>
            <button className="btn btn-danger float-right btn-sm" style={{fontSize: '18px'}}
                    onClick={() => this.props.deleteComment(this.props.comment.id)}>Delete
            </button>
            <button className="btn btn-primary float-right btn-sm">Edit</button>
          </div>
        </div>
        {this.state.showReplyForm && <AddReply storeReply={this.storeReply} users={this.state.users} parentCommentId={this.props.comment.comment_id} />}
        {this.state.replies.map((replies) => <Replies deleteComment={this.deleteComment} addLike={this.addLike} getUser={this.getUser} comment={replies} key={replies.comment_id}/>)}
      </div>
    )
  }
}
export default Replies
