import React, { Component } from 'react'
import './index.css'
import profile from "./profile.png";

class EditComment extends Component {

  storeEdit= (data) => {
    alert("got here")
    this.props.apiService.updateComment(data).then(() => {
      this.props.fetchReplies()
      this.props.toggleShowEditForm()
    })
  }


  render() {
    return (
      <div className="container edits">
        <div className="card my-3 edits">
          <div className="card-body edits">
            <div className="cf edits">
              <img src={profile} alt="pic" className="pic"/>
              <h2 className="card-title">{this.props.userInfo.poster_name}</h2>
              <textarea className="edit-box" name="body" onChange={this.props.handleFieldChange}>{this.props.userInfo.body}</textarea>
              <button onClick={this.storeEdit} className="btn btn-success float-right">Update Comment</button>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default EditComment
